import React, { useEffect, useState } from 'react';

function NewsList() {
  const [news, setArticles] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      try {
        setLoading(true);
        const res = await fetch('http://localhost:5000/api/articles');
        const data = await res.json();
        setArticles(data);
        setError(null);
      } catch (error) {
        console.error('Error fetching data:', error);
        setError('Failed to fetch news articles');
      } finally {
        setLoading(false);
      }
    };
    fetchData();
  }, []);

  if (loading) {
    return (
      <div className="p-6 text-center">
        <div className="text-xl text-gray-600">Loading news articles...</div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="p-6 text-center">
        <div className="text-xl text-red-600">{error}</div>
      </div>
    );
  }

  return (
    <div className="p-6">
      <div className="bg-white p-6 rounded-lg shadow-md">
        <ul className="space-y-4">
          {news.map((item, idx) => (
            <li key={idx} className="p-4 border-b last:border-b-0 hover:bg-gray-50 transition-colors">
              <strong className="text-xl text-gray-800 block mb-2">{item.time}</strong>
              <span className="text-lg text-gray-600">{item.title}</span>
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
}

export default NewsList;
