import React from 'react';
import { Link } from 'react-router-dom';

function Home() {
  return (
    <div className="p-6 bg-gray-50 min-h-screen text-center">
      <h1 className="text-4xl font-bold mb-6 text-gray-700">Welcome to Geo News Visualizer</h1>
      <p className="mb-8 text-lg text-gray-600">Explore news data visually and in list form.</p>
      <div className="space-x-4">
        <Link to="/chart" className="px-4 py-2 bg-blue-500 text-white rounded hover:bg-blue-600 transition">View Chart</Link>
        <Link to="/news" className="px-4 py-2 bg-green-500 text-white rounded hover:bg-green-600 transition">View News List</Link>
      </div>
    </div>
  );
}

export default Home;
